// Toggle burger menu const menuToggle = document.getElementById('mobile-menu'); const navLinks = document.querySelector('.nav-links'); menuToggle.addEventListener('click', () => { navLinks.classList.toggle('active'); }); // Testimonial slider let currentIndex = 0; let testimonials = document.querySelectorAll('.testimonial-item'); let interval; let isPaused = false; function showTestimonial(index) { testimonials.forEach((item, i) => { item.style.display = i === index ? 'flex' : 'none'; }); } function startTestiSlider() { interval = setInterval(() => { currentIndex = (currentIndex + 1) % testimonials.length; showTestimonial(currentIndex); }, 1500); } function stopSlider() { clearInterval(interval); } document.getElementById('pauseSliderBtn').addEventListener('click', function () { if (isPaused) { startTestiSlider(); this.textContent = '⏸ Pause'; } else { stopSlider(); this.textContent = '▶️ Play'; } isPaused = !isPaused; }); showTestimonial(currentIndex); startTestiSlider(); // Event Slider const eventSlides = document.querySelectorAll('.event-slide'); const dotContainer = document.getElementById('event-dots'); let eventIndex = 0; function showEventSlide(index) { eventSlides.forEach((slide, i) => { slide.classList.toggle('active', i === index); }); Array.from(dotContainer.children).forEach((dot, i) => { dot.classList.toggle('active-dot', i === index); }); } function createDots() { eventSlides.forEach((_, i) => { const dot = document.createElement('span'); dot.addEventListener('click', () => { eventIndex = i; showEventSlide(eventIndex); }); dotContainer.appendChild(dot); }); } function startEventSlider() { setInterval(() => { eventIndex = (eventIndex + 1) % eventSlides.length; showEventSlide(eventIndex); }, 2000); } // Initialize createDots(); showEventSlide(eventIndex); startEventSlider(); // Inline scripts from HTML pages // academic.html const menuButtons = document.querySelectorAll('.academic-menu button'); const sections = document.querySelectorAll('.academic-section'); menuButtons.forEach(btn => { btn.addEventListener('click', () => { menuButtons.forEach(b => b.classList.remove('active')); btn.classList.add('active'); sections.forEach(sec => sec.classList.remove('active')); document.getElementById(btn.dataset.target).classList.add('active'); }); }); // Slider let slideIndex = 0; const slides = document.querySelectorAll('#heroSlider img'); function showSlides() { slides.forEach(slide => slide.classList.remove('active')); slideIndex = (slideIndex + 1) % slides.length; slides[slideIndex].classList.add('active'); } setInterval(showSlides, 3000); // faculty.html // Simple Photo Slider let currentIndex = 0; const slider = document.getElementById('photoSlider'); setInterval(() => { currentIndex = (currentIndex + 1) % 3; slider.style.transform = `translateX(-${currentIndex * 100}%)`; }, 3000); document.querySelectorAll('.dept-filter button').forEach(button => { button.addEventListener('click', () => { document.querySelectorAll('.faculty-section').forEach(section => section.classList.remove('active')); document.getElementById(button.dataset.dept).classList.add('active'); }); }); document.querySelectorAll('.search-box').forEach(input => { input.addEventListener('input', e => { const target = e.target.dataset.target; const keyword = e.target.value.toLowerCase(); document.querySelectorAll(`#${target} .faculty-card`).forEach(card => { const content = card.textContent.toLowerCase(); card.style.display = content.includes(keyword) ? 'block' : 'none'; }); }); }); document.querySelectorAll('.faculty-card').forEach(card => { card.addEventListener('click', () => { document.getElementById('modalImg').src = card.dataset.img; document.getElementById('modalName').textContent = card.dataset.name; document.getElementById('modalTitle').textContent = card.dataset.title; document.getElementById('modalBio').textContent = card.dataset.bio; document.getElementById('facultyModal').style.display = 'flex'; }); }); function closeModal() { document.getElementById('facultyModal').style.display = 'none'; }